func getUserNameAndAddTask(_ task: Task) {
        view.isUserInteractionEnabled = false
        activityIndicator.startAnimating()
        var scQuery = SCQuery(collection: 
users
)
        scQuery.fields([
username
])
        scQuery.equalTo(
_id
, SCString(task.user))
        scQuery.find() {
            success, error, result  in
            if success 
 (result?.count)! 
 0 {
                if let username = (result?.values.first as? [String: Any])?[
username
] as? String {
                    task.username = username
                }
            } else {
                self.showAlert(title: 
Ошибка!
, message: 
Не найдено имя пользователя для задачи \(task.name)
, completion: nil)
            }
            self.taskList.append(task)
            //sort by fields: isClosed, closeDate
            self.taskList.sort(by: {t1, t2 in
                if t1.isClose == t2.isClose {
                    return t1.closeDate.compare(t2.closeDate) == ComparisonResult.orderedAscending
                }
                return !t1.isClose 
 t2.isClose })
            self.tableView.reloadData()
            self.view.isUserInteractionEnabled = true
            self.activityIndicator.stopAnimating()
        }
    }

